// src/components/Navbar.jsx
import React, { useState } from "react";
import { Link, useLocation } from "react-router-dom";
import { Menu, X } from "lucide-react";
import { Button } from "@/components/ui/button";

/**
 * Navbar — dark theme
 * - expects a logo at /logo.jpg
 * - exports a named `Navbar` (your pages import { Navbar } so keep it named)
 */
export const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const location = useLocation();

  const navLinks = [
    { name: "Home", path: "/" },
    { name: "Serial Killers", path: "/serial-killers" },
    { name: "Cold Cases", path: "/cold-cases" },
    { name: "Court Judgments", path: "/court-judgments" },
    { name: "About", path: "/about" },
    { name: "Contact", path: "/contact" },
  ];

  const isActive = (path) => location.pathname === path;

  return (
    <nav className="sticky top-0 z-50 bg-[#0d0d0f] text-white/95 border-b border-[#191919] shadow-sm">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">
          {/* Logo + title */}
          <Link to="/" className="flex items-center gap-3">
            <div className="w-10 h-10 rounded-md bg-gradient-to-br from-[#8f1919] to-[#c73535] flex items-center justify-center shadow">
              <span className="text-white font-extrabold text-lg select-none">CC</span>
            </div>

            <span className="hidden sm:inline-block font-semibold text-lg text-white">
              Crime &amp; Cranage
            </span>
          </Link>

          {/* Desktop nav */}
          <div className="hidden md:flex items-center space-x-3">
            {navLinks.map((link) => (
              <Link key={link.path} to={link.path}>
                <Button
                  variant="ghost"
                  className={`px-4 py-2 rounded-md transition-all duration-200 ${
                    isActive(link.path)
                      ? "bg-[#7a1717] text-white shadow-sm"
                      : "text-white/70 hover:text-white hover:bg-[#111214] hover:shadow-none"
                  }`}
                >
                  <span className="text-sm font-medium">{link.name}</span>
                </Button>
              </Link>
            ))}
          </div>

          {/* Mobile menu button */}
          <div className="md:hidden flex items-center">
            <button
              onClick={() => setIsOpen((v) => !v)}
              aria-label="Toggle menu"
              className="p-2 rounded-md text-white/90 hover:text-white hover:bg-[#111214]"
            >
              {isOpen ? <X size={20} /> : <Menu size={20} />}
            </button>
          </div>
        </div>
      </div>

      {/* Mobile menu */}
      {isOpen && (
        <div className="md:hidden bg-[#0d0d0f] border-t border-[#191919]">
          <div className="px-3 pt-2 pb-4 space-y-1">
            {navLinks.map((link) => (
              <Link key={link.path} to={link.path} onClick={() => setIsOpen(false)}>
                <div
                  className={`block px-3 py-2 rounded-md text-base font-medium transition-colors ${
                    isActive(link.path)
                      ? "bg-[#7a1717] text-white"
                      : "text-white/70 hover:text-white hover:bg-[#111214]"
                  }`}
                >
                  {link.name}
                </div>
              </Link>
            ))}
          </div>
        </div>
      )}
    </nav>
  );
};
