// src/pages/ContactPage.jsx
import { useEffect, useRef, useState } from 'react';
import { gsap } from 'gsap';
import { Navbar } from '@/components/Navbar';
import { Footer } from '@/components/Footer';
import {
  Card,
  CardContent,
  CardHeader,
  CardTitle,
} from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Label } from '@/components/ui/label';
import { Mail, Phone, MapPin, Send } from 'lucide-react';
import { toast } from 'sonner';

const ContactPage = () => {
  const authorRef = useRef(null);
  const formRef = useRef(null);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    message: '',
  });

  useEffect(() => {
    window.scrollTo(0, 0);

    gsap.from(authorRef.current, {
      opacity: 0,
      x: -100,
      duration: 1,
      ease: 'power3.out',
    });

    gsap.from(formRef.current, {
      opacity: 0,
      x: 100,
      duration: 1,
      ease: 'power3.out',
      delay: 0.3,
    });
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((s) => ({ ...s, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.name || !formData.email || !formData.message) {
      toast.error('Please fill in all required fields');
      return;
    }

    // TODO: Replace with real API call
    toast.success("Message sent successfully! We'll get back to you soon.");

    setFormData({ name: '', email: '', subject: '', message: '' });
  };

  return (
    <div className="min-h-screen bg-[#0b0b0c] text-white">
      <Navbar />

      {/* Header / hero with background image (public/Arpit.png) */}
      <section className="relative py-20">
        <div
          className="absolute inset-0 bg-cover bg-center"
          style={{
            backgroundImage: `linear-gradient(180deg, rgba(0,0,0,0.65), rgba(0,0,0,0.85)), url('/Arpit.png')`,
          }}
          aria-hidden="true"
        />
        <div className="relative max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <Badge className="bg-[#a00000] text-white mb-4 inline-block">Get in Touch</Badge>
          <h1 className="text-4xl sm:text-5xl md:text-6xl font-bold text-white mb-4">Contact Us</h1>
          <p className="text-lg text-gray-300 max-w-3xl mx-auto">
            Have a story tip, feedback, or inquiry? We'd love to hear from you. Reach out to our editorial team.
          </p>
        </div>
        {/* subtle overlay */}
        <div className="absolute inset-0 pointer-events-none" />
      </section>

      {/* Main content */}
      <section className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
          {/* Left: Author / contact info */}
          <div ref={authorRef} className="space-y-8">
            <Card className="bg-[#0b0b0c] border border-[#222] overflow-hidden">
              <div className="relative h-64">
                {/* use public/Arpit.png — using root-relative path so CRA serves it from public */}
                <img src="/Arpit.png" alt="Arpit Dev" className="w-full h-full object-cover" />
                <div className="absolute inset-0 bg-gradient-to-t from-[#0b0b0c]/80 to-transparent" />
              </div>

              <CardHeader className="-mt-20 relative z-10 px-6">
                <CardTitle className="text-3xl font-bold text-white mb-1">Arpit Dev</CardTitle>
                <p className="text-gray-300">Journalist & Crime Reporter</p>
              </CardHeader>

              <CardContent className="px-6 pb-6 pt-2">
                <p className="text-gray-300 leading-relaxed mb-4">
                  Dedicated to crime reporting, documentation and investigative journalism. If you have a tip or evidence,
                  please contact us safely.
                </p>
              </CardContent>
            </Card>

            {/* Contact Info */}
            <Card className="bg-[#0b0b0c] border border-[#222]">
              <CardHeader className="px-6 pt-6">
                <CardTitle className="text-2xl font-bold text-white">Contact Information</CardTitle>
              </CardHeader>

              <CardContent className="px-6 pb-6 space-y-4">
                <div className="flex items-start gap-3">
                  <div className="w-10 h-10 rounded-full bg-[#a00000]/10 flex items-center justify-center flex-shrink-0">
                    <Mail className="w-5 h-5 text-[#a00000]" />
                  </div>
                  <div>
                    <p className="font-semibold text-white">Email</p>
                    <p className="text-gray-300">devarpit04@gmail.com</p>
                    <p className="text-gray-400 text-sm">For story tips & editorial inquiries</p>
                  </div>
                </div>

                <div className="flex items-start gap-3">
                  <div className="w-10 h-10 rounded-full bg-[#a00000]/10 flex items-center justify-center flex-shrink-0">
                    <Phone className="w-5 h-5 text-[#a00000]" />
                  </div>
                  <div>
                    <p className="font-semibold text-white">Phone</p>
                    <p className="text-gray-300">+91 79924 05363</p>
                    <p className="text-gray-400 text-sm">(Mon–Fri, 10 AM – 6 PM IST)</p>
                  </div>
                </div>

                <div className="flex items-start gap-3">
                  <div className="w-10 h-10 rounded-full bg-[#a00000]/10 flex items-center justify-center flex-shrink-0">
                    <MapPin className="w-5 h-5 text-[#a00000]" />
                  </div>
                  <div>
                    <p className="font-semibold text-white">Office</p>
                    <p className="text-gray-300">Manatu, Ranchi, Jharkhand</p>
                    <p className="text-gray-400 text-sm">835222, India</p>
                  </div>
                </div>
              </CardContent>
            </Card>

            <Card className="bg-[#120000] border border-[#330000]">
              <CardContent className="p-6">
                <p className="text-white font-semibold mb-2">⏱ Quick Response Time</p>
                <p className="text-gray-300">
                  We typically respond to all inquiries within 24–48 hours. For urgent tips, mark the subject "URGENT".
                </p>
              </CardContent>
            </Card>
          </div>

          {/* Right: Contact form */}
          <div ref={formRef}>
            <Card className="bg-[#0b0b0c] border border-[#222] sticky top-24">
              <CardHeader className="px-6 pt-6">
                <CardTitle className="text-3xl font-bold text-white">Send Us a Message</CardTitle>
              </CardHeader>

              <CardContent className="px-6 pb-6">
                <form onSubmit={handleSubmit} className="space-y-6">
                  <div>
                    <Label htmlFor="name" className="text-sm text-gray-200 block mb-2">Name *</Label>
                    <Input
                      id="name"
                      name="name"
                      value={formData.name}
                      onChange={handleChange}
                      required
                      className="bg-[#0b0b0c] border border-[#333] text-white"
                    />
                  </div>

                  <div>
                    <Label htmlFor="email" className="text-sm text-gray-200 block mb-2">Email *</Label>
                    <Input
                      id="email"
                      name="email"
                      type="email"
                      value={formData.email}
                      onChange={handleChange}
                      required
                      className="bg-[#0b0b0c] border border-[#333] text-white"
                    />
                  </div>

                  <div>
                    <Label htmlFor="subject" className="text-sm text-gray-200 block mb-2">Subject</Label>
                    <Input
                      id="subject"
                      name="subject"
                      value={formData.subject}
                      onChange={handleChange}
                      className="bg-[#0b0b0c] border border-[#333] text-white"
                    />
                  </div>

                  <div>
                    <Label htmlFor="message" className="text-sm text-gray-200 block mb-2">Message *</Label>
                    <Textarea
                      id="message"
                      name="message"
                      rows={6}
                      value={formData.message}
                      onChange={handleChange}
                      required
                      className="bg-[#0b0b0c] border border-[#333] text-white"
                    />
                  </div>

                  <Button type="submit" size="lg" className="w-full bg-[#a00000] hover:bg-[#8b0000] text-white">
                    <Send className="inline mr-2" /> Send Message
                  </Button>
                </form>
              </CardContent>
            </Card>
          </div>
        </div>
      </section>

      <Footer />
    </div>
  );
};

export default ContactPage;
