// Updated ContactPage.jsx with Arpit Dev details
import { useEffect, useRef, useState } from 'react';
import { gsap } from 'gsap';
import { Navbar } from '@/components/Navbar';
import { Footer } from '@/components/Footer';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Label } from '@/components/ui/label';
import { Mail, Phone, MapPin, Send } from 'lucide-react';
import { toast } from 'sonner';

const ContactPage = () => {
  const authorRef = useRef(null);
  const formRef = useRef(null);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    message: '',
  });

  useEffect(() => {
    window.scrollTo(0, 0);

    gsap.from(authorRef.current, {
      opacity: 0,
      x: -100,
      duration: 1,
      ease: 'power3.out',
    });

    gsap.from(formRef.current, {
      opacity: 0,
      x: 100,
      duration: 1,
      ease: 'power3.out',
      delay: 0.3,
    });
  }, []);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.name || !formData.email || !formData.message) {
      toast.error('Please fill in all required fields');
      return;
    }

    toast.success("Message sent successfully! We'll get back to you soon.");

    setFormData({ name: '', email: '', subject: '', message: '' });
  };

  return (
    <div className="min-h-screen bg-background">
      <Navbar />

      {/* Header */}
      <section className="relative py-20 bg-gradient-to-b from-[hsl(var(--card))] to-background">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <Badge className="bg-[hsl(var(--primary))] text-primary-foreground mb-4">Get in Touch</Badge>
          <h1 className="text-5xl md:text-6xl font-bold text-foreground mb-6">Contact Us</h1>
          <p className="text-xl text-muted-foreground max-w-3xl mx-auto">
            Have a story tip, feedback, or inquiry? We'd love to hear from you.
          </p>
        </div>
      </section>

      {/* Main Content */}
      <section className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-16">
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">

          {/* Author Info */}
          <div ref={authorRef} className="space-y-8">

            <Card className="bg-card border-border overflow-hidden">
              <div className="relative h-64">
                <img
                  src="/public/Arpit.png"
                  alt="Journalist"
                  className="w-full h-full object-cover"
                />
                <div className="absolute inset-0 bg-gradient-to-t from-background via-background/60 to-transparent"></div>
              </div>
              <CardHeader className="-mt-20 relative z-10">
                <CardTitle className="text-3xl font-bold text-foreground mb-2">Arpit Dev</CardTitle>
                <CardDescription className="text-lg text-muted-foreground">
                  Journalist & Crime Reporter
                </CardDescription>
              </CardHeader>
              <CardContent>
                <p className="text-muted-foreground leading-relaxed">
                  Dedicated to crime reporting, documentation and investigative journalism.
                </p>
              </CardContent>
            </Card>

            {/* Contact Details */}
            <Card className="bg-card border-border">
              <CardHeader>
                <CardTitle className="text-2xl font-bold text-foreground">Contact Information</CardTitle>
              </CardHeader>
              <CardContent className="space-y-4">

                <div className="flex items-start gap-3">
                  <div className="w-10 h-10 rounded-full bg-[hsl(var(--primary))]/10 flex items-center justify-center flex-shrink-0">
                    <Mail className="w-5 h-5 text-[hsl(var(--primary))]" />
                  </div>
                  <div>
                    <p className="font-semibold text-foreground">Email</p>
                    <p className="text-muted-foreground">devarpit04@gmail.com</p>
                    <p className="text-muted-foreground text-sm">For story tips & inquiries</p>
                  </div>
                </div>

                <div className="flex items-start gap-3">
                  <div className="w-10 h-10 rounded-full bg-[hsl(var(--primary))]/10 flex items-center justify-center flex-shrink-0">
                    <Phone className="w-5 h-5 text-[hsl(var(--primary))]" />
                  </div>
                  <div>
                    <p className="font-semibold text-foreground">Phone</p>
                    <p className="text-muted-foreground">+91 79924 05363</p>
                    <p className="text-muted-foreground text-sm">(Mon-Fri, 10 AM - 6 PM IST)</p>
                  </div>
                </div>

                <div className="flex items-start gap-3">
                  <div className="w-10 h-10 rounded-full bg-[hsl(var(--primary))]/10 flex items-center justify-center flex-shrink-0">
                    <MapPin className="w-5 h-5 text-[hsl(var(--primary))]" />
                  </div>
                  <div>
                    <p className="font-semibold text-foreground">Office</p>
                    <p className="text-muted-foreground">Manatu, Ranchi, Jharkhand</p>
                    <p className="text-muted-foreground text-sm">, India</p>
                  </div>
                </div>

              </CardContent>
            </Card>

            {/* Response */}
            <Card className="bg-[hsl(var(--primary))]/10 border-[hsl(var(--primary))]">
              <CardContent className="p-6">
                <p className="text-foreground font-semibold mb-2">⏱ Quick Response</p>
                <p className="text-muted-foreground">
                  Expect a reply within 24–48 hours. Mark subject as "URGENT" if time-sensitive.
                </p>
              </CardContent>
            </Card>

          </div>

          {/* Contact Form */}
          <div ref={formRef}>
            <Card className="bg-card border-border sticky top-24">
              <CardHeader>
                <CardTitle className="text-3xl font-bold text-foreground">Send Us a Message</CardTitle>
                <CardDescription className="text-lg text-muted-foreground">
                  Fill out the form and we’ll get back to you.
                </CardDescription>
              </CardHeader>

              <CardContent>
                <form onSubmit={handleSubmit} className="space-y-6">

                  <div className="space-y-2">
                    <Label htmlFor="name" className="text-foreground">Name *</Label>
                    <Input id="name" name="name" value={formData.name} onChange={handleChange} required />
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="email" className="text-foreground">Email *</Label>
                    <Input id="email" name="email" type="email" value={formData.email} onChange={handleChange} required />
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="subject" className="text-foreground">Subject</Label>
                    <Input id="subject" name="subject" value={formData.subject} onChange={handleChange} />
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="message" className="text-foreground">Message *</Label>
                    <Textarea id="message" name="message" rows={6} value={formData.message} onChange={handleChange} required />
                  </div>

                  <Button type="submit" size="lg" className="w-full bg-[hsl(var(--primary))] text-primary-foreground">
                    <Send size={20} className="mr-2" /> Send Message
                  </Button>

                </form>
              </CardContent>
            </Card>
          </div>

        </div>
      </section>

      <Footer />
    </div>
  );
};

export default ContactPage;
