export const BreakingNewsTicker = () => {
  const breakingNews = [
    'üö® BREAKING: New evidence uncovered in decades-old cold case',
    '‚ö° EXCLUSIVE: Serial killer patterns analyzed by forensic experts',
    'üì∞ UPDATE: Major development in high-profile court judgment',
    'üîç INVESTIGATION: Unsolved mysteries from India\'s criminal history',
    '‚öñÔ∏è JUSTICE: Landmark verdict changes course of criminal law',
  ];

  // Duplicate for seamless loop
  const tickerContent = [...breakingNews, ...breakingNews];

  return (
    <div className="ticker-wrapper">
      <div className="ticker-content">
        {tickerContent.map((news, index) => (
          <span
            key={index}
            className="text-foreground font-semibold text-sm whitespace-nowrap"
          >
            {news}
          </span>
        ))}
      </div>
    </div>
  );
};
