// src/pages/ContactPage.jsx
import { useEffect, useRef, useState } from 'react';
import { gsap } from 'gsap';
import { Navbar } from '@/components/Navbar';
import { Footer } from '@/components/Footer';
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Label } from '@/components/ui/label';
import { Mail, Phone, MapPin, Send } from 'lucide-react';
import { toast } from 'sonner';

const ContactPage = () => {
  const authorRef = useRef(null);
  const formRef = useRef(null);

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    message: '',
  });

  const [sending, setSending] = useState(false);

  useEffect(() => {
    window.scrollTo(0, 0);

    gsap.from(authorRef.current, {
      opacity: 0,
      x: -120,
      duration: 1,
      ease: 'power3.out',
    });

    gsap.from(formRef.current, {
      opacity: 0,
      x: 120,
      duration: 1,
      ease: 'power3.out',
      delay: 0.2,
    });
  }, []);

  const handleChange = (e) => {
    setFormData((d) => ({ ...d, [e.target.name]: e.target.value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.name || !formData.email || !formData.message) {
      toast.error("Please fill all required fields.");
      return;
    }

    setSending(true);

    setTimeout(() => {
      toast.success("Message Sent! We'll respond as soon as possible.");
      setSending(false);
      setFormData({
        name: '',
        email: '',
        subject: '',
        message: '',
      });
    }, 900);
  };

  return (
    <div className="min-h-screen bg-background">
      <Navbar />

      {/* ---------- HEADER ---------- */}
      <section className="relative py-20">
        <div
          className="absolute inset-0 bg-cover bg-center"
          style={{
            backgroundImage:
              "linear-gradient(180deg, rgba(0,0,0,0.75), rgba(0,0,0,0.88)), url('./public/Arpit.png')",
          }}
        />

        <div className="relative text-center max-w-4xl mx-auto px-6">
          <Badge className="bg-[hsl(var(--primary))] text-primary-foreground mb-4 px-5 py-1">
            Contact • Crime & Carnage
          </Badge>

          <h1 className="text-5xl md:text-6xl font-bold text-foreground tracking-tight">
            Contact Us
          </h1>

          <p className="mt-4 text-lg text-muted-foreground max-w-2xl mx-auto">
            Have any story tip, inquiry, or feedback? Reach out to our editorial team anytime.
          </p>
        </div>
      </section>

      {/* ---------- MAIN CONTENT ---------- */}
      <section className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-16 grid grid-cols-1 lg:grid-cols-2 gap-12">

        {/* ---------------- LEFT SIDE: AUTHOR + INFO ---------------- */}
        <div ref={authorRef} className="space-y-10">

          {/* AUTHOR CARD */}
          <Card className="bg-card border-border overflow-hidden">
            <div className="relative h-64">
              <img src="public/Arpit.png" alt="Arpit Dev" className="w-full h-full object-cover" />
              <div className="absolute inset-0 bg-gradient-to-t from-background to-transparent" />
            </div>

            <CardHeader className="-mt-20 relative z-10">
              <CardTitle className="text-3xl font-bold text-foreground">Arpit Dev Mishra</CardTitle>
              <CardDescription className="text-muted-foreground text-lg">
                Crime Journalist • Investigator
              </CardDescription>
            </CardHeader>

            <CardContent>
              <p className="text-muted-foreground leading-relaxed">
                Specializing in criminal investigations, forensic documentation and crime analysis.
                Passionate about revealing stories hidden beneath the surface.
              </p>
            </CardContent>
          </Card>

          {/* CONTACT INFORMATION SECTION */}
          <Card className="bg-card border-border">
            <CardHeader>
              <CardTitle className="text-2xl font-bold text-foreground">
                Contact Information
              </CardTitle>
            </CardHeader>

            <CardContent className="space-y-6">

              {/* Email */}
              <div className="flex items-start gap-4">
                <div className="w-11 h-11 rounded-full bg-[hsl(var(--primary))]/10 flex items-center justify-center">
                  <Mail className="text-[hsl(var(--primary))] w-5 h-5" />
                </div>

                <div>
                  <p className="text-foreground font-semibold">Email</p>
                  <p className="text-muted-foreground">devarpit04@gmail.com</p>
                </div>
              </div>

              {/* Phone */}
              <div className="flex items-start gap-4">
                <div className="w-11 h-11 rounded-full bg-[hsl(var(--primary))]/10 flex items-center justify-center">
                  <Phone className="text-[hsl(var(--primary))] w-5 h-5" />
                </div>

                <div>
                  <p className="text-foreground font-semibold">Phone</p>
                  <p className="text-muted-foreground">+91 79924 05363</p>
                  <p className="text-muted-foreground text-sm">(Mon–Fri, 10 AM – 6 PM IST)</p>
                </div>
              </div>

              {/* Office */}
              <div className="flex items-start gap-4">
                <div className="w-11 h-11 rounded-full bg-[hsl(var(--primary))]/10 flex items-center justify-center">
                  <MapPin className="text-[hsl(var(--primary))] w-5 h-5" />
                </div>

                <div>
                  <p className="text-foreground font-semibold">Office</p>
                  <p className="text-muted-foreground">Manatu, Ranchi, Jharkhand</p>
                  <p className="text-muted-foreground text-sm">835222 – India</p>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Quick Response */}
          <Card className="bg-[hsl(var(--primary))]/10 border-[hsl(var(--primary))]">
            <CardContent className="p-6">
              <p className="text-foreground font-semibold mb-2">⏱ Quick Response</p>
              <p className="text-muted-foreground">
                We typically reply within 24–48 hours.  
                For urgent crime tips, please mark your subject line as **URGENT**.
              </p>
            </CardContent>
          </Card>
        </div>

        {/* ---------------- RIGHT SIDE: FORM ---------------- */}
        <div ref={formRef}>
          <Card className="bg-card border-border sticky top-24">
            <CardHeader>
              <CardTitle className="text-3xl font-bold text-foreground">Send Us a Message</CardTitle>
              <CardDescription className="text-muted-foreground text-lg">
                Fill out this form and we will get back to you soon.
              </CardDescription>
            </CardHeader>

            <CardContent>
              <form onSubmit={handleSubmit} className="space-y-6">

                {/* Name */}
                <div className="space-y-2">
                  <Label htmlFor="name" className="text-foreground">Name *</Label>
                  <Input
                    id="name"
                    name="name"
                    required
                    value={formData.name}
                    onChange={handleChange}
                  />
                </div>

                {/* Email */}
                <div className="space-y-2">
                  <Label htmlFor="email" className="text-foreground">Email *</Label>
                  <Input
                    id="email"
                    name="email"
                    type="email"
                    required
                    value={formData.email}
                    onChange={handleChange}
                  />
                </div>

                {/* Subject */}
                <div className="space-y-2">
                  <Label htmlFor="subject" className="text-foreground">Subject</Label>
                  <Input
                    id="subject"
                    name="subject"
                    value={formData.subject}
                    onChange={handleChange}
                  />
                </div>

                {/* Message */}
                <div className="space-y-2">
                  <Label htmlFor="message" className="text-foreground">Message *</Label>
                  <Textarea
                    id="message"
                    name="message"
                    rows={5}
                    required
                    value={formData.message}
                    onChange={handleChange}
                  />
                </div>

                {/* Submit */}
                <Button
                  type="submit"
                  size="lg"
                  disabled={sending}
                  className="w-full bg-[hsl(var(--primary))] hover:bg-red-700 text-primary-foreground"
                >
                  <Send className="w-5 h-5 mr-2" />
                  {sending ? 'Sending...' : 'Send Message'}
                </Button>
              </form>
            </CardContent>
          </Card>
        </div>

      </section>

      <Footer />
    </div>
  );
};

export default ContactPage;
